import { world_map } from './world-map';
import { uncountries } from './data';
import * as React from "react";
import { MapsComponent, LayersDirective, LayerDirective, Inject, Legend, MapsTooltip, Zoom, Selection, MarkersDirective, MarkerDirective, Marker  } from '@syncfusion/ej2-react-maps';
import Content from './Content';
class App extends React.Component {
    render() {
        return (
          <MapsComponent id="maps"  legendSettings={{ visible: true }} theme="Material" zoomSettings={{
            enable: true,
            color: 'green',
            highlightColor: 'blue',
            selectionColor: 'orange',
            horizontalAlignment: 'Far',
            mouseWheelZoom: false,
            toolbars: ['ZoomIn', 'ZoomOut', 'Pan', 'Reset']}}

          >

             <Inject services={[Legend, MapsTooltip, Zoom, Selection, Marker]}/>
             <LayersDirective>
                <LayerDirective 
                  shapeData={world_map} 
                  shapeDataPath='Country' 
                  shapePropertyPath='name' 
                  /*
                  tooltipSettings={{
                    visible: true,
                    valuePath: 'name'
                  }}
                  */
                  animationDuration={500}
                  selectionSettings={{
                    enable: true,
                    fill: 'gray',
                    border: { color: 'black', width: 1 }
                }}

                >
                  <h1 style={{color: 'red'}}>World Map</h1>
                  <MarkersDirective> 
                    <MarkerDirective 
                      visible={true} 
                      height={10} width={10} 
                      fill="#3386dc" shape="Circle" 
                      tooltipSettings={{
                        visible: true,
                        valuePath: 'display'
                      }} 
                      dataSource={[{ latitude: 20.95121990866204, longitude: 73.468749999999998, display: <Content />  }]}
                      >
                    </MarkerDirective>
                    <MarkerDirective 
                      visible={true} 
                      height={10} width={10} 
                      fill="#3386dc" shape="Circle" 
                      tooltipSettings={{
                        visible: true,
                        valuePath: 'city'
                      }}
                      dataSource={[{ latitude: 50.88893689676585, longitude: -80.3359375, city: 'Canada' }]}
                      >
                    </MarkerDirective>
                    <MarkerDirective 
                      visible={true} 
                      height={10} width={10} 
                      fill="#3386dc" shape="Circle" 
                      tooltipSettings={{
                        visible: true,
                        valuePath: 'city'
                      }}
                      dataSource={[{ latitude: -14.64607562172573, longitude: 35.54687499999999, city: 'Malawi' }]}>
                    </MarkerDirective>
                  </MarkersDirective>
                </LayerDirective>
            </LayersDirective>
        </MapsComponent>
          );
    }
}
export default App;
