import { world_map } from './world-map';
import { uncountries } from './data';
import * as React from "react";
import { MapsComponent, LayersDirective, LayerDirective, Inject, Legend, MapsTooltip, Zoom   } from '@syncfusion/ej2-react-maps';
class App extends React.Component {
    render() {
        return (
          <MapsComponent id="maps" legendSettings={{ visible: true }} theme="Material" zoomSettings={{
            enable: true,
            color: 'green',
            highlightColor: 'gray',
            selectionColor: 'orange',
            horizontalAlignment: 'Center',
            toolbars: ['ZoomIn', 'ZoomOut', 'Pan', 'Reset']}}
          >
             <Inject services={[Legend, MapsTooltip, Zoom]}/>
             <LayersDirective>
                <LayerDirective 
                  shapeData={world_map} 
                  shapeDataPath='Country' 
                  shapePropertyPath='name' 
                  dataSource={uncountries}
                  tooltipSettings={{
                    visible: true,
                    valuePath: 'name'
                  }}
                >
                </LayerDirective>
            </LayersDirective>
        </MapsComponent>
          );
    }
}
export default App;
